#ifndef _CONIO_H_#define _CONIO_H_#include <_mingw.h>                      /// ConIO implementation for Mingw/C++#include <windows.h>extern "C"{#ifndef RC_INVOKED__ _CRTIMP char* __cdecl __MINGW_NOTHROW _cgets( char* ); _CRTIMP int __cdecl __MINGW_NOTHROW _cprintf( const char*,... ),                                    _cputs( const char* ),                                   _cscanf( char*,... ),                                  _kbhit(),kbhit(),                                  _getch(),getch(),                                 _getche(),getche(),                               _putch(int),putch(int),                             _ungetch(int),ungetch(int);#endiftypedef enum{ BLACK,BLUE,GREEN,CYAN,RED,MAGENTA,BROWN,LIGHTGRAY,DARKGRAY,              LIGHTBLUE,LIGHTGREEN,LIGHTCYAN,LIGHTRED,LIGHTMAGENTA,YELLOW,WHITE            } COLORS; void clrscr(),clreol(); void gotoxy( int x,int y );           // при x или y<=0 их отсчеты сохраняются
 void textsize( int w,int h,int bh=0 ); // размеры экрана, укрытая длина буфера void textattr( unsigned int attr ); void textcolor( COLORS clr ),textbackground( COLORS cbk ); COORD wherexy(); int wherex(),wherey();  // Титульная надпись консоли
 void texttitle( const char* title );     // { SetConsoleTitle( title ); }}inline void textcolor( COLORS clr,COLORS cbk ){ textattr( clr|cbk<<4 ); }
int print( int x,int y,const char*,... );int print( const char*,... );
#endif